define({"topics" : [{"title":"Set Call Forwarding Prompt","shortdesc":"\n                  <p class=\"shortdesc\">By default, when the PBX is forwarding an incoming call to another number, the PBX         will play the call forwarding prompt\n                     \"please hold when I try to locate the person you are         calling\", and then play the MoH music when the caller is waiting.\n                     You can disable the call         forwarding prompt and change the MoH music to a normal ring tone. In this way, the caller\n                     will not realize that the call is forwarded.\n                  </p>\n               ","id":"task_dg2_spq_5cb","href":"topic\/set_call_forwarding_prompt.html","menu": {"hasChildren":false,},"tocID":"task_dg2_spq_5cb-d46e5732","topics":[]},{"title":"Activate\/Deactivate Call Forwarding","shortdesc":"\n                  <p class=\"shortdesc\">Extension users can dial the Call Forwarding feature codes on their phones to activate     or deactivate Call Forwarding function.</p>\n               ","id":"topic_tn3_1sb_5cb","href":"topic\/activate_or_deactivate_call_forwarding.html","menu": {"hasChildren":false,},"tocID":"topic_tn3_1sb_5cb-d46e5805","topics":[]}]});